import React, { useContext, useState } from "react";
import { PortfolioContext } from "../context/PortfolioContext";
import PortfolioForm from "../forms/PortfolioForm";

function PortfolioManagement() {
  const { portfolios, setPortfolios } = useContext(PortfolioContext);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingPortfolio, setEditingPortfolio] = useState(null);

  const handleEdit = (index) => {
    setEditingIndex(index);
    setEditingPortfolio(portfolios[index]);
  };

  const handleDelete = (index) => {
    const updatedPortfolios = portfolios.filter((_, i) => i !== index);
    setPortfolios(updatedPortfolios);
  };

  const handleFormSubmit = (updatedPortfolio) => {
    const updatedPortfolios = [...portfolios];
    updatedPortfolios[editingIndex] = updatedPortfolio;
    setPortfolios(updatedPortfolios);
    setEditingIndex(null);
    setEditingPortfolio(null);
  };

  return (
    <div className="portfolio-management">
      <h2>Portfolio Management</h2>
      <PortfolioForm
        onSubmit={handleFormSubmit}
        initialValues={editingPortfolio}
      />
      <ul>
        {portfolios.map((portfolio, index) => (
          <li key={index}>
            {portfolio.name} - ${portfolio.value}
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PortfolioManagement;
